import React, { Component } from 'react';
import * as lyricService from '../services/lyricService'

export default class LyricsList extends Component {

    constructor(props) {
        super(props)

        this.state = {
            items: []
        }
        this.getLyrics = this.getLyrics.bind(this)
    }

    componentDidMount() {
        this.getLyrics();
    }

    getLyrics() {
        lyricService.getAll()
            .then(response => {
                this.setState({
                    items: response.items
                })
            })
    }

    redner() {

        const lyricsList = this.state.items.map(item => {
            return (
                <div>
                    <div>{item.id}</div>
                    <div>{item.lyrics}</div>
                </div>
            )
        })
        return (
            <div>
                {lyricsList}
            </div>
        )
    }
}
