import React, { Component } from "react";
import { BrowserRouter, Route, NavLink } from "react-router-dom";
import NotificationSystem from "react-notification-system";
import "./App.css";
import logo from './logo.svg';
import LyricsForm from "../forms/lyricsForm";

export default class Header extends Component {
  constructor(props) {
    super(props);

    this.notify = this.notify.bind(this);
  }
  notificationSystem = null;

  notify(configSettings) {
    const notifyConfig = {
      message: "Notification Message",
      level: "success",
      ...configSettings
    };
    this.notificationSystem.addNotification(notifyConfig);
  }

  componentDidMount() {
    this.notificationSystem = this.refs.notificationSystem;
  }

  render() {
    return (
      <BrowserRouter>
        <div className="App container">
          <nav className="navbar navbar-inverse navbar-fixed-top">
            <div className="container">
              <div className="navbar-header">
                <button
                  type="button"
                  className="navbar-toggle collapsed"
                  datatoggle="collapse"
                  datatarget="#navbar"
                  ariaexpanded="false"
                  ariacontrols="navbar"
                >
                  <span className="sr-only">Toggle navigation</span>
                  <span className="icon-bar" />
                  <span className="icon-bar" />
                  <span className="icon-bar" />
                </button>
                
                <a className="navbar-brand" href="/">
                  <img src={logo} className="App-logo" alt="logo" style={ {height: '20px', display: 'inline'}} />
                  Lyrics Og
                </a>
              </div>
              <div id="navbar" className="collapse navbar-collapse">
                <ul className="nav navbar-nav">
                  <li>
                    <NavLink to="/lyrics-form">lyrics</NavLink>
                  </li>
                </ul>
              </div>
            </div>
          </nav>
          <div style={{ marginTop: "60px" }}>
            <div>

              <Route path="/lyrics-form" component={LyricsForm}/> 


            </div>
          </div>

          <NotificationSystem ref="notificationSystem" />
        </div>
      </BrowserRouter>
    );
  }
}
